import { ref, onMounted, onUnmounted } from 'vue';
import request from "@/utils/request";
import { useRoute } from 'vue-router';
export default {
  __name: 'VideoDetail',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const videoData = ref(null);
    const videoPlayer = ref(null);
    const numberOfEpisodes = ref(0);
    const videoCanvas = ref(null);
    let canvasContext = null;
    const route = useRoute();
    const videoId = route.params.id;
    const videoName = route.params.name;
    const loadVideoData = async () => {
      try {
        const res = await request.get(`/file/${videoId}`);
        videoData.value = res.data.data;
      } catch (error) {
        console.error('Error loading video data:', error);
      }
    };
    const loadNumberOfEpisodes = async () => {
      try {
        const res = await request.get('/videos/diversity', {
          params: {
            name: videoName
          }
        });
        numberOfEpisodes.value = res.data.data;
      } catch (error) {
        console.error('Error loading number of episodes:', error);
      }
    };
    onMounted(() => {
      loadVideoData();
      loadNumberOfEpisodes();
      initializeCanvas();
    });
    const initializeCanvas = () => {
      videoCanvas.value = document.querySelector('.video-canvas');
      if (!videoCanvas.value) return; // 提前返回，以防止 videoCanvas 为 null
      canvasContext = videoCanvas.value.getContext('2d');
    };
    const loadEpisode = async episodeNumber => {
      try {
        const res = await request.get(`/videos/${videoName}/episode/${episodeNumber}`);
        const url = res.data.data;
        if (videoPlayer.value) {
          videoPlayer.value.src = url;
          videoPlayer.value.load();
        }
      } catch (error) {
        console.error('Error loading episode:', error);
      }
    };
    const handleVideoLoaded = () => {
      if (videoPlayer.value) {
        videoPlayer.value.play();
      }
    };
    const renderVideoFrameToCanvas = () => {
      if (!videoPlayer.value || !canvasContext || videoPlayer.value.paused || videoPlayer.value.ended) return; // 添加条件以避免错误
      canvasContext.clearRect(0, 0, videoCanvas.value.width, videoCanvas.value.height); // 清除画布

      console.log("videoCanvas.value.width====", videoCanvas.value.width);
      canvasContext.drawImage(videoPlayer.value, 0, 0, videoCanvas.value.width, videoCanvas.value.height);
      applyBlurEffect();
    };
    const applyBlurEffect = () => {
      if (!canvasContext) return; // 提前返回以避免错误
      canvasContext.filter = 'blur(10px)'; // 应用模糊效果
    };

    // 监听视频播放事件，确保在视频播放时渲染视频帧到 Canvas
    const handleVideoPlay = () => {
      requestAnimationFrame(renderVideoFrameToCanvas); // 使用 requestAnimationFrame 代替 setInterval，以优化性能
    };

    // 监听视频播放事件
    onMounted(() => {
      if (videoPlayer.value) {
        videoPlayer.value.addEventListener('play', handleVideoPlay);
      }
    });

    // 在组件销毁时移除事件监听器
    onUnmounted(() => {
      if (videoPlayer.value) {
        videoPlayer.value.removeEventListener('play', handleVideoPlay);
      }
    });
    const __returned__ = {
      videoData,
      videoPlayer,
      numberOfEpisodes,
      videoCanvas,
      get canvasContext() {
        return canvasContext;
      },
      set canvasContext(v) {
        canvasContext = v;
      },
      route,
      videoId,
      videoName,
      loadVideoData,
      loadNumberOfEpisodes,
      initializeCanvas,
      loadEpisode,
      handleVideoLoaded,
      renderVideoFrameToCanvas,
      applyBlurEffect,
      handleVideoPlay,
      ref,
      onMounted,
      onUnmounted,
      get request() {
        return request;
      },
      get useRoute() {
        return useRoute;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
