import { ref, onMounted } from 'vue';
import { useRoute } from 'vue-router';

// 创建响应式变量来存储视频信息

export default {
  __name: 'VideoDetail',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const videoName = ref('');
    const videoUrl = ref('');
    const route = useRoute();

    // 加载视频数据的函数
    const loadVideo = () => {
      const id = route.params.id;
      const products = JSON.parse(route.params.Products);
      const foundVideo = products.find(product => product.id === id);
      if (foundVideo) {
        // 更新响应式变量的值
        videoName.value = foundVideo.name;
        videoUrl.value = foundVideo.url;
      } else {
        console.error('Video not found');
      }
    };
    onMounted(() => {
      loadVideo();
    });
    const __returned__ = {
      videoName,
      videoUrl,
      route,
      loadVideo,
      ref,
      onMounted,
      get useRoute() {
        return useRoute;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
