        // 捕获并处理请求错误
        console.error('Error loading product detail:', error);
      }
    };
    onMounted(loadProductDetail); // 在组件挂载后加载产品详情数据

    const __returned__ = {
      route,
      productId,
      product,
      loadProductDetail,
      ref,
      onMounted,
      get request() {
        return request;
      },
      get useRouter() {
        return useRouter;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
