import { ref, watchEffect } from 'vue';
import { useRoute } from 'vue-router';

// 创建响应式变量来存储视频信息

export default {
  __name: 'VideoDetail',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const videoName = ref('');
    const videoUrl = ref('');
    const route = useRoute();

    // 监听路由变化，并加载视频数据
    watchEffect(() => {
      loadVideo();
    });

    // 加载视频数据的函数
    const loadVideo = () => {
      if (!route.params || !route.params.id || !route.params.Products) {
        console.error('Missing route parameters');
        return;
      }
      const id = route.params.id;
      const productsString = route.params.Products;
      console.log("================", route.params.Products);
      const products = JSON.parse(decodeURIComponent(productsString));
      const foundVideo = products.find(product => product.id == id); // 注意这里使用 == 而不是 ===
      if (foundVideo) {
        videoName.value = foundVideo.name;
        videoUrl.value = foundVideo.url;
      } else {
        console.error('Video not found');
      }
    };
    const __returned__ = {
      videoName,
      videoUrl,
      route,
      loadVideo,
      ref,
      watchEffect,
      get useRoute() {
        return useRoute;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
